import React, { useState, useContext } from 'react';
import { AppContext } from '../context/AppContext';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';

const Login = () => {
  const { setUser, setLoggedIn } = useContext(AppContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const history = useHistory();

  const logIn = async (username, password, e) => {
    e.preventDefault();
    await axios({
      method: 'POST',
      url: `/users/login`,
      data: {
        username: username,
        password: password
      }
    })
      .then((user) => {
        setUser(user.data.user);
        setUsername(user.username);
        localStorage.setItem('token', user.data.token);
        setLoggedIn(true);
      })
      .catch((e) => {
        console.log(e.message.toString(), 'Credentials Error');
      });
  };

  return (
    <>
      <div className="login-div">
        <form
          className="login-form"
          onSubmit={(e) => logIn(username, password, e)}
        >
          <input
            type="username"
            name="username"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="btn-sm btn-primary actions">
            Log In
          </button>
          <div>
            <p>
              Don't have an account? &nbsp;
              <Link to="/signup" onClick={() => history.push(`/signup`)}>
                Sign Up
              </Link>
            </p>
          </div>
        </form>
      </div>
    </>
  );
};

export default Login;
